mapper.select:
select ifnull(count(1), 0)
from `orders`
where del_flag = 0
  and shop_id = #{shopId}
  and create_time > #{queryDate}
  and order_status = 30

select DATE_FORMAT(o.create_time, '%Y-%m-%d') date, o.id o_id, DATE_FORMAT(o.create_time, '%H:%i') time, c.avatar, c.nickname, o.commission from orders o left join xjs.customer c on c.customer_id = o.customer_id where o.share_id = #{dto.customerId}
and o.create_time > #{dto.queryDate} and o.create_time < date_add(#{dto.queryDate},INTERVAL 1 DAY)
order by o.create_time desc
